import React, {useEffect, useState} from 'react'
import '../styling/register.css'
import { Link, useNavigate } from 'react-router-dom'

export default function SignUpPage() {

    const[userName, setUserName] = useState('')
    const[password, setPassword] = useState('')
    const[condition, setCondition] = useState('')
    const[nurse, setNurse] = useState('')
    const[nurses, setNurses] = useState([])
    const[errors, setErrors] = useState([])

    const navigate = useNavigate()

    const signUpData = {name: userName, password: password, condition: condition, nurse_id: nurse}

    useEffect(() => {
        fetch('/nurses',{
            mehod: 'GET'
        })
        .then(response => response.json())
        .then(data => setNurses(data))
    },[])

    const allNurses = nurses.map((nurse) => {
        return(
            <option key={nurse.id} value={nurse.id}>{nurse.speciality}</option>
        )
    })

    function handleSignUp(e){
        e.preventDefault()
        fetch('/patients',{
            method: 'POST',
            headers: {
                'Content-Type':'application/json'
            },
            body: JSON.stringify(signUpData)
        })
        .then(response => {
            if(response.ok){
                response.json().then((newPatient) => console.log(newPatient))
                navigate('/home')
            }else{
                response.json().then((errorData) => setErrors(errorData))
            }
        })

    }

    return (
        <div id='signup-page'>

            <form id="signup-form" onSubmit={handleSignUp}>

                <h2 style={{textAlign: 'center'}}>Sign Up</h2>

                {
                    errors.length > 0 && (
                        <ul style={{color: 'red'}}>{errors.map((error,index) => {
                            <li ></li>
                        })}</ul>
                    )
                }

                <div className='row mb-3'>
                    <label className="form-label">Username</label>
                    <input className="form-control" type="text" placeholder="Enter username" required onChange={e => setUserName(e.target.value)}/>
                </div>

                <div className='row mb-3'>
                    <label className="form-label">Password</label>
                    <input  className="form-control"type="password" autoComplete='true' placeholder=" password" required onChange={e => setPassword(e.target.value)}/>
                </div>

                <div className='row mb-3'>

                    <div className='col-6'>
                        <label className="form-label">Medical condition</label>
                        <input  className="form-control"type="text" placeholder="Enter condition" required onChange={e => setCondition(e.target.value)}/>
                    </div>

                    <div className='col-6'>
                        <label className="form-label">Nurse</label>
                        <select  className="form-select" onChange={e => setNurse(e.target.value)}>
                            <option >Choose...</option>
                            {allNurses}
                        </select> 
                    </div>

                </div>

                <p className='form-text'>Already have an account ?<Link to='/'>Back to Log in</Link></p>

                <div style={{textAlign: 'center'}}>
                    <button className="btn btn-primary" type="submit" style={{width: '300px'}}>Sign Up</button>
                </div>

            </form>
        </div>
    )

}
