import React, {useState} from 'react'
import '../styling/forgotpassword.css'
import { Link } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'

export default function ForgetPasswordPage() {

    const[userName, setUserame] = useState('')
    const[password, setPassword] = useState('')
    const[errors, setErrors] = useState([])

    const resetData = {password}

    function handleResetPassword(e){
        e.preventDefault()
        fetch(`/reset-password/${userName}`,{
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(resetData)
        })
        .then(response => {
            if(response.ok){
                response.json().then(data => console.log(data))
            }else{
                response.json().then(errorData => setErrors(errorData))
            }
        })
    }

    return (
        <div id="forgot-password-page">

            <h3 className="reset-p">To receive reset instructions:</h3>

            <form id='reset-password-form' onSubmit={handleResetPassword}>

                <h2>Reset your password</h2>

                <div className='row mb-3'>
                    <label className="form-label">Username</label><br/>
                    <input className="form-control" type="text" placeholder="Enter username" required onChange={e => setUserame(e.target.value)}/>
                </div>

                <div className='row mb-3'>
                    <label className="form-label">Password</label><br/>
                    <input className="form-control" type="password" placeholder="Enter Password" required onChange={e => setPassword(e.target.value)}/>
                </div>

                <div>
                    <button className="btn btn-primary" type="submit">Reset password</button>
                </div>

                <p className='form-text'>Dont have an account ?<Link to="/register">Create an account</Link>.</p>
                <p className='form-text'><Link to="/">Back to Login</Link>.</p>

            </form>

        </div>
    )
}
