import React, { useState, useEffect } from 'react';
import '../styling/createappointment.css';
import { useNavigate } from 'react-router-dom';

export default function CreateAppointment() {
	const [doctor, setDoctor] = useState(null);
	const [date, setDate] = useState('');
	const [duration, setDuration] = useState('');
	const [appointment, setAppointment] = useState('');
	const [doctors, setDoctors] = useState([]);
	const [errors, setErrors] = useState([]);

	const navigate = useNavigate();

	useEffect(() => {
		fetch('https://quikhealth-l66y.onrender.com/doctors',)
			.then((response) => response.json())
			.then((data) => setDoctors(data));
	}, []);

	const allDoctors = doctors.map((doctor) => {
		return (
			<option key={doctor.id} value={doctor.id}>
				{doctor.name}
			</option>
		);
	});

	const appointmentData = {
		doctor_id: doctor,
		date,
		duration,
		appointment_type: appointment,
	};

	function handleCreate(e) {
		e.preventDefault();
		fetch('https://quikhealth-l66y.onrender.com/create_appointment', {
			method: 'post',
			headers: {
				'Content-Type': 'application/json',
			},
			credentials: 'include',
			body: JSON.stringify(appointmentData)
		}).then((response) => {
			if (response.ok) {
				response.json().then((data) => console.log(data));
				navigate('/appointments');
			} else {
				response.json().then((errorData) => setErrors(errorData.errors));
			}
		});
	}

	return (
		<div id="create-appointment-page">
			<form id="create-form" onSubmit={handleCreate}>
				<h1>Book an Appointment</h1>

				{errors.length > 0 && (
					<ul style={{ color: 'red' }}>
						{errors.map((error, index) => {
							return <li key={index}>{error}</li>;
						})}
					</ul>
				)}

				<div className="row mb-3">
					<label className="form-label">Select Doctor</label>
					<select
						className="form-select"
						onChange={(e) => setDoctor(e.target.value)}
					>
						<option>Choose Doctor ....</option>
						{allDoctors}
					</select>
				</div>

				<div className="row mb-3">
					<label className="form-label">Date</label>
					<input
						type="date"
						className="form-control"
						required
						onChange={(e) => setDate(e.target.value)}
					/>
				</div>

				<div className="row mb-3">
					<label className="form-label">Duration</label>
					<input
						type="text"
						placeholder="e.g 30 minutes"
						required
						onChange={(e) => setDuration(e.target.value)}
					/>
				</div>

				<div className="row mb-3">
					<label className="form-label">Type of Appointment</label>
					<input
						type="text"
						placeholder="e.g Consultation"
						required
						onChange={(e) => setAppointment(e.target.value)}
					/>
				</div>

				<div style={{ textAlign: 'center' }}>
					<button
						className="btn btn-primary"
						style={{ width: '150px' }}
						type="submit"
					>
						Book
					</button>
				</div>
			</form>
		</div>
	);
}
